nta.declaration:
// Place global declarations here.
const int AgentNum = 1;
typedef int[0,AgentNum-1] AgentScale;
const int TaskNum = 10;
const int EventNum = 0;
const int MilestoneNum = 11;
const int MaxIteration = 1;

bool position[AgentNum][MilestoneNum]={{false,false,false,false,false,false,false,false,false,false,false}};
bool tf[AgentNum][TaskNum]={{true,false,false,false,false,false,false,false,false,false}};
bool ts[AgentNum][TaskNum]={{true,false,false,false,false,false,false,false,false,false}};


bool isBusy(int taskID)
{
    int id = 0;
    bool busy = false;

    for(id = 0; id < AgentNum; id++)
    {
        if(ts[id][taskID])
        {
            busy = true;
            return busy;
        }
    }

    return busy;
}

const int rules[MilestoneNum]={0,1,2,3,4,5,5,6,7,8,9};
const int RegularTaskNum[AgentNum] = {9};
chan move[AgentNum], initialize;

int iteration[AgentNum] = {0};

clock globalTime;

template.declaration:
clock t;

void updateIteration()
{
	int i=0;
	bool finish = true;
	for(i=1;i<=RegularTaskNum[id];i++)
	{
		finish &= tf[id][i];
	}
	if(finish)
	{
		for(i=1;i<=RegularTaskNum[id];i++)
		{
			tf[id][i]=false;
		}
		
		iteration[id]+=1;
		if(iteration[id]>=MaxIteration)
		{
			iteration[id]=MaxIteration;
		}
	}
	else
	{
    	finish = true;
	}
}

nta.system:
movement0 = Movement0(0);
taskExe0 = TaskExecution0(0);

system movement0, taskExe0;
